/*Table structure for table `users` */

DROP TABLE IF EXISTS `users`;

CREATE TABLE `users` (
  `uid` int(11) NOT NULL AUTO_INCREMENT COMMENT '用户id，自增主键',
  `username` varchar(50) NOT NULL DEFAULT '' COMMENT '用户名',
  `password` varchar(255) NOT NULL DEFAULT '' COMMENT '密码',
  `phone` varchar(25) NOT NULL DEFAULT '' COMMENT '手机号',
  `nickname` varchar(20) NOT NULL DEFAULT '' COMMENT '昵称',
  `createTime` datetime NOT NULL DEFAULT '1970-01-01 00:00:00' COMMENT '注册时间',
  `lastLoginTime` datetime DEFAULT NULL COMMENT '上一次登录时间',
  `loginCount` int(11) NOT NULL DEFAULT '0' COMMENT '登录次数',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态：0-正常，1-禁用，2-已删除',
  `openid` varchar(50) DEFAULT NULL COMMENT '微信openid',
  PRIMARY KEY (`uid`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

